mapper.select:
select count(*) 
from comments
where board_num = #{board_num}

select *
from( select rownum rnum, c.*
	from(
		select num, id, content, reg_date, board_num
		from comments
		where board_num = #{board_num}
		order by reg_date desc
	) c
	where rownum < = #{end}
)
where rnum between #{start} and #{end}

mapper.update:
update comments
set content = #{content}
where num = #{num}
